#9Quelle est la position dans l'array de la personne @epenser
def epenser
  epenser = @ary.find_index("@epenser")
  puts "epenser se trouve à la position #{epenser}"
end

#8sortbycinquante
def sortbycinquante
  arr = @ary.sort_by {|x| x.length}
  puts arr[0..49].inspect
end

#7alphabétique
def alphabétique
a = @ary.sort_by{|x| x.downcase }
puts a
end

#6nbr_underscore
def nbr_underscore
  joined =  @ary.join
  puts "#{joined.count('_')}"
end

#5counting all string in array that contains a only uppercase
def nbr_string_upper
 maj = @ary.grep(/[A-Z]/)
 puts "#{maj.length}"
end

#4 maj_begin
def maj_begin
capital_letters = ('A'..'Z')
maj = @ary.count {|x| capital_letters.include?(x[1])}
puts "#{maj}"
end

#3
def aude
  sans_maj = @ary.grep(/[Aa]ude/)
  puts "#{sans_maj.length}"
end

#2
def handle_content_num
  maj = @ary.select {|x| x=~ /[123456789]/ }
  puts "#{maj.length}"
end

#1 nbr_journalist
def nbr_journalist
  puts "nbr_journalist est :"
  x=@ary.count
  puts "#{x}"
end

user_input = ""
while user_input !=9
  puts "MENU PRINCIPAL"
  puts ""

  puts "tapez '1' pour afficher le nombre de journalistes dans ce array"
  puts "tapez '2' pour afficher le nombre handle que contiennent un numéro ?"
  puts "tapez '3' pour afficher le nombre d'handle que contiennent les 4 lettres du prénom Aude à la suite sans MAJ"
  puts "tapez '4' pour afficher le nombre qui commencent par une majuscule (première lettre juste après le @) ?"
  puts "tapez '5' pour afficher combien contiennent une majuscule ? "
  puts "tapez '6' pour afficher combien contiennent de underscore _ dans tous les pseudos confondus?"
  puts "tapez '7' pour trier la liste de handle par ordre alphabétique"
  puts "tapez '8' pour afficher les 50 handles les plus courts de ce array"
  puts "tapez '9' pour afficher la position dans l'array de la personne @epenser"

  puts  "Entrez votre selection"
  print "> "
  user_input = gets.to_i

  if user_input == 1
    puts "nombre de journalistes dans ce array est : "
    nbr_journalist
  elsif user_input == 2
    puts "le nombre handle que contiennent un numéro : "
      handle_content_num
  elsif user_input == 3
    puts "le nombre d'handle que contiennent les 4 lettres du prénom Aude à la suite sans MAJ:"
    aude
  elsif user_input == 4
    puts "le nombre qui commencent par une majuscule (première lettre juste après le @)"
    maj_begin
  elsif user_input == 5
    puts "contiennent une majuscule"
    nbr_string_upper
  elsif user_input == 6
  puts "contiennent de underscore _ dans tous les pseudos confondus"
    nbr_underscore
  elsif user_input == 7
    puts "Trie la liste de handle par ordre alphabétique"
    alphabétique
  elsif user_input == 8
    puts "affichage des 50 handles les plus courts de ce array"
    sortbycinquante
  elsif user_input == 9
    puts "la position dans l'array de la personne @epenser"
    epenser
  else
  puts "out"
  end

end
